import React, { useState, useEffect } from 'react';
import './audio.css';
import Nav from './nav';
import UserFooter from './userfooter';

const Audio = () => {
  // State management
  const [recording, setRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [popupMessage, setPopupMessage] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    dataType: ""
  });
  const [results, setResults] = useState(null);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle file upload
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      setAudioBlob(null); // Clear recorded audio if a file is selected
    }
  };

  // Start recording
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (e) => chunks.push(e.data);
      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: "audio/wav" });
        setAudioBlob(blob);
        stream.getTracks().forEach(track => track.stop());
      };

      recorder.start();
      setMediaRecorder(recorder);
      setRecording(true);
    } catch (error) {
      console.error("Error accessing microphone:", error);
      setPopupMessage("Error accessing microphone. Please check permissions.");
    }
  };

  // Stop recording
  const stopRecording = () => {
    if (mediaRecorder && mediaRecorder.state !== "inactive") {
      mediaRecorder.stop();
      setRecording(false);
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!audioBlob && !selectedFile) {
      setPopupMessage("Please record or upload an audio file.");
      return;
    }

    if (!formData.name || !formData.dataType) {
      setPopupMessage("Please fill in all required fields.");
      return;
    }

    setIsProcessing(true);
    setPopupMessage("Processing audio... Please wait.");

    const formData = new FormData();
    formData.append("name", formData.name);
    formData.append("dataType", formData.dataType);

    if (audioBlob) {
      formData.append("audio", audioBlob, "recording.wav");
    } else if (selectedFile) {
      formData.append("audio", selectedFile);
    }

    try {
      const response = await fetch("http://127.0.0.1:5000/process_audio", { // Replace with your actual backend URL
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setResults(data);
    } catch (error) {
      console.error("Error during fetch:", error);
      setPopupMessage("Failed to process audio. Please try again later.");
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <>
      <Nav />
      <div className="audiocontainer">
        <div className="audiocard">
          <h2>Submit Your Audio</h2>
          <form onSubmit={handleSubmit}>
            {/* ... form fields */}
          </form>
        </div>
        <div className="audiocard rules">
          {/* ... rules and conditions */}
        </div>
        {popupMessage && (
          <div className="popup">
            {popupMessage}
            <button onClick={() => setPopupMessage("")} className="close-popup">
              ×
            </button>
          </div>
        )}
        {results && (
          <div>
            {results.status === 'success' ? (
              <div>
                <p>Transcribed Text: {results.transcribed_text}</p>
                {/* Render predictions using a map or other suitable structure */}
                {results.predictions.map((prediction) => (
                  <p key={prediction.location}>
                    {prediction.location} - {prediction.district} ({prediction.confidence})
                  </p>
                ))}
              </div>
            ) : (
              <p>Error: {results.message}</p>
            )}
          </div>
        )}
      </div>
      <UserFooter />
    </>
  );
};

export default Audio;
