import React, { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Mic, Upload } from "lucide-react";

const Audio = () => {
  const [recording, setRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState(null);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (e) => chunks.push(e.data);
      recorder.onstop = () => {
        setAudioBlob(new Blob(chunks, { type: "audio/wav" }));
      };

      recorder.start();
      setMediaRecorder(recorder);
      setRecording(true);
    } catch (err) {
      setError("Could not access microphone");
    }
  };

  const stopRecording = () => {
    mediaRecorder.stop();
    setRecording(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsProcessing(true);
    setError(null);

    try {
      const formData = new FormData();
      
      // Get the file either from the file input or recorded audio
      const fileInput = e.target.querySelector('input[type="file"]');
      const audioFile = fileInput.files[0] || audioBlob;

      if (!audioFile) {
        throw new Error("Please provide an audio file or recording");
      }

      // Add form data
      formData.append("file", audioFile);
      formData.append("name", e.target.name.value);
      formData.append("dataType", e.target.dataType.value);

      // Send to backend API
      const response = await fetch("http://localhost:8000/api/process-audio", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("Failed to process audio");
      }

      const data = await response.json();

      if (data.success) {
        setPrediction(data.prediction);
      } else {
        throw new Error(data.error || "Failed to process audio");
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      <div className="max-w-4xl mx-auto space-y-6">
        <Card>
          <CardHeader>
            <CardTitle>Submit Your Audio</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="space-y-2">
                <label className="text-sm font-medium">Name:</label>
                <Input type="text" name="name" required />
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium">Type of Data:</label>
                <Select name="dataType" required>
                  <SelectTrigger>
                    <SelectValue placeholder="Select type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="Local Legends and Stories">Local Legends and Stories</SelectItem>
                    <SelectItem value="Cultural Explanations">Cultural Explanations</SelectItem>
                    <SelectItem value="Folk Songs">Folk Songs</SelectItem>
                    <SelectItem value="Language and Dialects">Language and Dialects</SelectItem>
                    <SelectItem value="Traditional Crop Information">Traditional Crop Information</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium">Upload Audio:</label>
                <Input type="file" accept="audio/*" className="file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-medium" />
              </div>

              <div className="flex justify-center">
                <Button
                  type="button"
                  onClick={recording ? stopRecording : startRecording}
                  variant={recording ? "destructive" : "secondary"}
                  className="flex items-center gap-2"
                >
                  <Mic className="w-4 h-4" />
                  {recording ? "Stop Recording" : "Record Audio"}
                </Button>
              </div>

              {audioBlob && (
                <Alert className="bg-green-50 border-green-200">
                  <AlertDescription>
                    Recording saved! You can submit now.
                  </AlertDescription>
                </Alert>
              )}

              <Button 
                type="submit" 
                disabled={isProcessing}
                className="w-full"
              >
                {isProcessing ? "Processing..." : "Submit"}
              </Button>
            </form>

            {error && (
              <Alert variant="destructive" className="mt-4">
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}

            {prediction && (
              <div className="mt-6 p-4 bg-slate-50 rounded-lg border">
                <h3 className="font-semibold mb-2">Prediction Results:</h3>
                <pre className="whitespace-pre-wrap text-sm">{prediction}</pre>
              </div>
            )}
          </CardContent>
        </Card>

        <Card>
